    glMatrixMode(GL_PROJECTION); // 设置矩阵模式为投影矩阵
    glLoadIdentity(); // 清空投影矩阵
    gluPerspective(45.0, 1.0, 1.0, 100.0); // 设置透视投影
}

void display() {
    glClear(GL_COLOR_BUFFER_BIT); // 清除颜色缓冲区
    // 其他绘制代码
