User.init({
    name: {
        type: DataTypes.STRING
    },
    email: {
        type: DataTypes.STRING
    }
}, {
    sequelize,
    modelName: 'User',
    indexes: [{
        fields: ['id'], // 指定索引字段
        unique: true // 设置唯一约束
    }],
    getPrimaryKeyField: function() {
        // 指定主键字段
        return 'id';
    }
});

// 直接指定主键字段
User.init({
    name: {
        type: DataTypes.STRING
    },
    email: {
        type: DataTypes.STRING
    }
}, {
    sequelize,
    modelName: 'User',
    tableName: 'User', // 指定表名
    underscored: false, // 禁用下划线模式
    paranoid: true, // 启用软删除
    timestamps: true, // 启用时间戳
    charset: 'utf8mb4', // 设置字符集
    collate: 'utf8mb4_unicode_ci', // 设置排序规则
    indexes: [{ // 指定索引
        fields: ['email']
    }],
    hooks: {}, // 指定钩子
    freezeTableName: false, // 禁用表名冻结
    omitNull: false, // 禁用忽略 null 值
    schema: 'public', // 指定模式
    schemaDelimiter: '.', // 指定模式分隔符
    tableName: 'User', // 指定表名
    indexes: [{ // 指定索引
