  props: {
    // 定义 props 类型
    propOne: Number,
    propTwo: {
      type: String,
      required: true,
      // 定义 prop 的默认值
      default: 'default value'
    },
    // 不可以有空值
    propThree: {
      type: String,
      // 定义 prop 的验证规则
