    {
        string domainName = "example.com";
        try
        {
            // Get the MX records for the domain
            MXRecord[] mxRecords = Dns.GetHostEntry(domainName).AddressList.SelectMany(addr => Dns.GetHostRecords(addr.ToString())).OfType<MXRecord>().ToArray();

            // Print the MX records
            if (mxRecords.Length > 0)
            {
                Console.WriteLine($"MX records for {domainName}:");
                foreach (var mxRecord in mxRecords)
