    public function __construct($name) {
        $this->name = $name;
    }

    // 一个实例方法，用于输出问候语
    public function sayHello() {
        echo "Hello, my name is " . $this->name;
    }
}

// 通过 new 关键字实例化一个 Person 对象，并传入初始化参数
$person = new Person("John");

// 使用 -> 操作符调用对象的 sayHello 方法
$person->sayHello();
?>
--------------------------------------------------

