using ICSharpCode.SharpZipLib.Zip;

ZipFile zipFile = new ZipFile("example.zip");
foreach (ZipEntry entry in zipFile)
{
    if (entry.IsDirectory)
    {
        // 该ZipEntry表示目录
        Console.WriteLine("目录:" + entry.Name);
    }
